import React from 'react'

import Select from 'react-select'
import makeAnimated from 'react-select/animated'
import { render } from 'react-dom';
// import { colourOptions } from '../data';


const optionsHot = [
    { value: 'testHot1', label: 'Test Hot Food 1' },
    { value: 'testHot2', label: 'Test Hot Food 2' },
    { value: 'testHot3', label: 'Test Hot Food 3' }
  ];


  class InputHot extends React.Component{
    state = {
      selectedOption: null,
    };
    handleChange = selectedOption => {
      this.setState(
        { selectedOption },
        () => console.log(`Hot food option selected:`, this.state.selectedOption)
      );
  };

  render(){
    const {selectedOption} = this.state;

    
    return(
      <Select
        value = {selectedOption}
        onChange = {this.handleChange}
        options = {optionsHot}
        closeMenuOnSelect={false}
        components={animatedComponents}
        isMulti={true} />
      );
    }
  }

  const animatedComponents = makeAnimated();

  export default InputHot
